/*Realizar el siguiente modelado para una ferretería para poder llevar el control de ventas y 
el seguimiento de los empleados en ventas OJO no tener en cuenta la tabla 
gama_producto no es necesaria.*/
--Creacion de tabla Producto
CREATE TABLE PRODUCTO (
    CODIGO_PRODUCTO VARCHAR2(15) PRIMARY KEY,
    NOMBRE VARCHAR2(70),
    DIMENSIONES VARCHAR2(25),
    PROVEEDOR VARCHAR2(50),
    DESCRIPCION CLOB,
    CANTIDAD_EN_STOCK NUMBER(6) NOT NULL,
    PRECIO_VENTA NUMBER(15,2) NOT NULL,
    PRECIO_PROVEEDOR NUMBER(15,2) NOT NULL
);


--Creacion de tabla Oficina
CREATE TABLE OFICINA (
    CODIGO_OFICINA NUMBER(10) PRIMARY KEY,
    CIUDAD VARCHAR2(30),
    PAIS VARCHAR2(50),
    REGION VARCHAR2(50),
    CODIGO_POSTAL VARCHAR2(10),
    TELEFONO VARCHAR2(20),
    LINEA_DIRECCION1 VARCHAR2(50),
    LINEA_DIRECCION2 VARCHAR2(50)
);

--Creacion de tabla Empleado
CREATE TABLE EMPLEADO (
    CODIGO_EMPLEADO NUMBER(11) PRIMARY KEY,
    NOMBRE VARCHAR2(50),
    APELLIDO1 VARCHAR2(50),
    APELLIDO2 VARCHAR2(50),
    EXTENSION VARCHAR2(10),
    EMAIL VARCHAR2(100),
    CODIGO_OFICINA NUMBER(10), -- Cambiado de VARCHAR2(10) a NUMBER(10)
    PUESTO VARCHAR2(50),
    CONSTRAINT FK_OFICINA FOREIGN KEY (CODIGO_OFICINA) REFERENCES OFICINA (CODIGO_OFICINA)
);

--Creacion de tabla Cliente
CREATE TABLE CLIENTE (
    CODIGO_CLIENTE NUMBER(11) PRIMARY KEY,
    NOMBRE_CLIENTE VARCHAR2(50),
    NOMBRE_CONTACTO VARCHAR2(50),
    APELLIDO_CONTACTO VARCHAR2(50),
    TELEFONO VARCHAR2(15),
    FAX VARCHAR2(15),
    LINEA_DIRECCION1 VARCHAR2(50),
    LINEA_DIRECCION2 VARCHAR2(50),
    CIUDAD VARCHAR2(50),
    REGION VARCHAR2(50),
    PAIS VARCHAR2(50),
    CODIGO_POSTAL VARCHAR2(10),
    CODIGO_EMPLEADO NUMBER(11),
    LIMITE_CREDITO NUMBER(15,2),
    CONSTRAINT FK_EMPLEADO FOREIGN KEY (CODIGO_EMPLEADO) REFERENCES EMPLEADO (CODIGO_EMPLEADO)
);

--Creacion de Tabla Pedido
CREATE TABLE PEDIDO (
    CODIGO_PEDIDO NUMBER(11) PRIMARY KEY,
    FECHA_PEDIDO DATE,
    FECHA_ESPERADA DATE,
    FECHA_ENTREGA DATE,
    ESTADO VARCHAR2(15),
    COMENTARIOS CLOB,
    CODIGO_CLIENTE NUMBER(11),
    CONSTRAINT FK_CLIENTE_PEDIDO FOREIGN KEY (CODIGO_CLIENTE) REFERENCES CLIENTE (CODIGO_CLIENTE)
);

--Creacion de Tabla Pago
CREATE TABLE PAGO (
    ID_TRANSACCION VARCHAR(50),
    CODIGO_CLIENTE NUMBER(11),
    FORMA_PAGO VARCHAR(40),
    FECHA_PAGO DATE,
    TOTAL NUMBER(15,2) NOT NULL,
    CONSTRAINT FK_CLIENTE FOREIGN KEY (CODIGO_CLIENTE) REFERENCES CLIENTE (CODIGO_CLIENTE)
);

--Agregacion de columna "ID_TRANSACCION
ALTER TABLE PAGO ADD ID_TRANSACCION VARCHAR(50);

--Creacion de tabla Detalle Pedido
CREATE TABLE DETALLE_PEDIDO (
    CODIGO_PEDIDO NUMBER(11),
    CODIGO_PRODUCTO VARCHAR2(15),
    CANTIDAD NUMBER(11) NOT NULL,
    PRECIO_UNIDAD NUMBER(15,2) NOT NULL,
    NUMERO_LINEA NUMBER(15,2) NOT NULL,
    CONSTRAINT FK_PEDIDO FOREIGN KEY (CODIGO_PEDIDO) REFERENCES PEDIDO (CODIGO_PEDIDO),
    CONSTRAINT FK_PRODUCTO FOREIGN KEY (CODIGO_PRODUCTO) REFERENCES PRODUCTO (CODIGO_PRODUCTO)
);

