--6. Realizar la consulta de cuál ha sido el cliente que a realizado más pagos
SELECT c.codigo_cliente, c.nombre_cliente, COUNT(p.id_transaccion) AS total_pagos
FROM cliente c
JOIN pago p ON c.codigo_cliente = p.codigo_cliente
GROUP BY c.codigo_cliente, c.nombre_cliente
ORDER BY total_pagos DESC
FETCH FIRST 1 ROWS ONLY;


--7. Consultar cual es el promedio de las ventas por empelado
SELECT empleado.codigo_empleado, empleado.nombre, empleado.apellido1, empleado.apellido2, empleado.puesto, ventas.total_ventas
FROM empleado
JOIN (
    SELECT cliente.codigo_empleado, SUM(detalle_pedido.cantidad * detalle_pedido.precio_unidad) AS total_ventas
    FROM cliente
    JOIN pedido ON cliente.codigo_cliente = pedido.codigo_cliente
    JOIN detalle_pedido ON pedido.codigo_pedido = detalle_pedido.codigo_pedido
    GROUP BY cliente.codigo_empleado
) ventas ON empleado.codigo_empleado = ventas.codigo_empleado
ORDER BY ventas.total_ventas DESC;


--8. sacar los empleados que han vendido más que el promedio de las ventas
SELECT e.codigo_empleado, e.nombre, e.apellido1, e.apellido2, e.puesto, ventas.total_ventas
FROM empleado e
JOIN (
    SELECT c.codigo_empleado, SUM(dp.cantidad * dp.precio_unidad) AS total_ventas
    FROM cliente c
    JOIN pedido p ON c.codigo_cliente = p.codigo_cliente
    JOIN detalle_pedido dp ON p.codigo_pedido = dp.codigo_pedido
    GROUP BY c.codigo_empleado
) ventas ON e.codigo_empleado = ventas.codigo_empleado
WHERE ventas.total_ventas > (
    SELECT AVG(total_ventas)
    FROM (
        SELECT c.codigo_empleado, SUM(dp.cantidad * dp.precio_unidad) AS total_ventas
        FROM cliente c
        JOIN pedido p ON c.codigo_cliente = p.codigo_cliente
        JOIN detalle_pedido dp ON p.codigo_pedido = dp.codigo_pedido
        GROUP BY c.codigo_empleado
    )
)
ORDER BY ventas.total_ventas DESC;

