--15. Cuál es el producto más vendido
SELECT p.codigo_producto, p.nombre, SUM(dp.cantidad) AS total_vendido
FROM producto p
JOIN detalle_pedido dp ON p.codigo_producto = dp.codigo_producto
GROUP BY p.codigo_producto, p.nombre
ORDER BY total_vendido DESC
FETCH FIRST 1 ROWS ONLY;

-- 16.  la oficina que más vende y el valor de las ventas en su totalidad
SELECT o.codigo_oficina, o.ciudad, o.pais, o.region, o.telefono, o.linea_direccion1, o.linea_direccion2,
       SUM(dp.cantidad * dp.precio_unidad) AS total_ventas
FROM oficina o
JOIN empleado e ON o.codigo_oficina = e.codigo_oficina
JOIN pedido p ON p.codigo_cliente IN (SELECT codigo_cliente FROM cliente WHERE codigo_empleado = e.codigo_empleado)
JOIN detalle_pedido dp ON p.codigo_pedido = dp.codigo_pedido
GROUP BY o.codigo_oficina, o.ciudad, o.pais, o.region, o.telefono, o.linea_direccion1, o.linea_direccion2
ORDER BY total_ventas DESC
FETCH FIRST 1 ROWS ONLY;


